def solve(A,B):
    if len(A) != len(B):
        return 0
    count_A = [0] * 26
    count_B = [0] * 26
    for i in range(len(A)):
        count_A[ord(A[i]) - ord('a')] += 1
        count_A[ord(B[i]) - ord('a')] += 1
    for i in range(26):
        if count_A[i] != count_B[i]:
            return 0
    return 1




A = ""
B = ""
print(len(B))
print(solve(A,B))